    function showGovernorateCustomers(town)
    {
        let Table1 = document.getElementById('myTable');
        let myTable = document.getElementById('myTable').getElementsByTagName('tbody')[0];

        if ($('#myTable tbody tr').length != 0)
        {
            $("#myTable tbody tr").remove();
        }

        const townMap = <?php echo json_encode($townMap); ?>;
        console.log("townMap is");
        console.log(townMap);
        let townCustomers = 0;
        for(let t=0; t < townMap.length; t++)
        {
            if(townMap[t].town == town)
            {
                let row = myTable.insertRow();
                let cell1 = row.insertCell(0);
                let cell2 = row.insertCell(1);

                cell1.innerHTML = "<td>"+townMap[t].governorate+"</td>";
                cell2.innerHTML = "<td>"+townMap[t].customers+"</td>";

                townCustomers += townMap[t].customers;
            }
        }
        document.getElementById("townTitle").innerHTML = town + " ("+ townCustomers +")";
        document.getElementById("guages").style.display = "none";
        document.getElementById("townData").style.display = "block";
    }

    function showGuages()
    {
        document.getElementById("townData").style.display = "none";
        document.getElementById("guages").style.display = "block";
    }
    
    
    function dateFiltering(tpe)
    {
        document.getElementById('filter_type').value = tpe;
        $("#dateFilter").modal("show");
    }
</script>

